INSERT [KS_Label] ([LabelID], [LabelName], [LabelContent], [Description], [ClassID], [OrderID], [LabelType], [LabelFlag], [AddDate], [From3G], [TemplateID], [JsonID]) VALUES (4230, N'T2_联系我们', N'            	<div class="regard">
