INSERT [KS_U_Article] ([InfoID], [Title], [ClassID], [Content], [PageTitle], [KeyTags], [Intro], [TitleFontColor], [Attribute], [DefaultPic], [TurnUrl], [priority], [Rank], [Hits], [HitsByDay], [HitsByWeek], [HitsByMonth], [LastHitsTime], [AddDate], [VoteID], [TemplateFile], [Template3GFile], [ShowON3G], [FileName], [RefreshTF], [Verify], [DelTF], [Inputer], [AttachmentCharge], [AttachmentChargeOnce], [SocialIDs], [CityID], [CountyID], [SEOTitle], [SEOKeyWords], [SEODescription], [CommentNum], [CopyFrom], [Author], [ProvinceID], [ArrGroupID], [ReadPoint], [ChargeType], [PitchTime], [ReadTimes], [DividePercent], [PKID], [InfoPurview], [Channelid], [GoodNum], [BadNum], [FavNum], [ShareNum]) VALUES (3381, N'江苏仪征环保官员连续8年举报当地化工污染', 1166, N'<p style="text-align:center;"><img alt="化工园区内难掩化工废气的味道 " src="http://i.kesion.com/UploadFiles/2013-11/74/b11302940471301562505IH.jpg"/></p><p class="pictext" style="text-align:center;">化工园区内难掩化工废气的味道</p><p style="TEXT-INDENT: 2em">63岁的侯宜中与位于江苏仪征的扬州化工园已“较劲”8年。</p><p style="TEXT-INDENT: 2em">退休前曾担任仪征市环保局党组书记的他，为举报扬州化工园企业污染和环保违法违规，连续8年向上级有关部门举报污染和问题，反应情况和建议，得到了上级领导和部门的重视，解决了一些问题，但问题依然存在。八年来，他累计撰写调研材料、信访件已近30万字。</p><p style="TEXT-INDENT: 2em">“为城市环境和百姓碧水蓝天，我会一直坚持下去，哪怕终老，直至仪征生态文明，百姓满意。”侯宜中在接受人民网记者采访时说道。记者上网查询得知，侯宜中与扬州化工园的博弈，早在2009年新华社等媒体就作过报道。几年来，化工园虽然重视了环保，不断加大了污染整治的力度，调整了产业发展规划，但化工废气还仍然没有彻底解决，城区仍能嗅着化工废气，当地居民称废气严重时不敢开窗。</p><p style="TEXT-INDENT: 2em">“我就是一个另类，哪有自己人揭自己的短？之所以如此是因为面对扬州化工园的问题，仪征市政府无能为力，市民不了解情况，也无从解决污染问题。”，侯宜中自嘲道。从2006年9月起，他便以仪征市环保局党组书记的身份，举报扬州化工园企业污染和环保违法违规问题。“一个曾经秀美的生态小城不能就这样被吞噬。”他表示。</p><p style="TEXT-INDENT: 2em">据媒体2009年报道，化工废气扰民是困扰仪征当地居民最大的问题，“遇有西南风和雾天，数万仪征市民嗅着难闻的恶臭”。气味来源主要是化工园区内的(方顺粮油、优士化学)两家企业，而化工企业排放的化学物质是(优士化学的排放物中，含有四氯化碳等物质，这些物质)被学界公认“对人体有致癌可能”。</p><p style="TEXT-INDENT: 2em">在被媒体曝光后，几年来，上述两家企业在治理废气排放上不断改进。侯宜中也曾表示，“比以前好多了，现在一发现化工废气扰民，仪征环保局就敢叫停了。”</p><p style="TEXT-INDENT: 2em">但污染问题并没有得到根本解决。人民网记者探访发现，扬州化工园邻近仪征主城区，在城区能隐约嗅到化工废气的味道。当地群众仍然反映，“园区企业化工废气排放严重时，眼睛睁不开，喉咙作痒，在家不敢开门窗。”</p><p style="TEXT-INDENT: 2em">(人民网)</p>', N'', N'', N'化工园区内难掩化工废气的味道 63岁的侯宜中与位于江苏仪征的扬州化工园已“较劲”8年。退休前曾担任仪征市环保局党组书记的他，为举报扬州化工园企业污染和环保违法违规，连续8年向上级有关部门举报污染和问题，反应情况和建议，得到了上级领导和部门的重视，解决了一些问', N'', N'0100000', N'http://i.kesion.com/UploadFiles/2013-11/74/b11302940471301562505IH.jpg', N'', 0, NULL, 104, 1, 1, 1, CAST(0x0000A27C00F8A00C AS DateTime), CAST(0x0000A27B0045C360 AS DateTime), 0, N'{$TemplateDir}/企业模板/相关常识内容页.html', N'{$TemplateDir}相关常识内容页.html', 1, N'news/gn/3381.html', 1, 1, 0, N'admin', 0, 0, CONVERT(TEXT, N''), 0, 0, N'', N'', N'', NULL, N'新浪网', N'壹壹', 0, N'', 0.0000, 0, 12, 10, 0, 0, 0, 1, 0, 0, 0, 0)
