INSERT [KS_U_Article] ([InfoID], [Title], [ClassID], [Content], [PageTitle], [KeyTags], [Intro], [TitleFontColor], [Attribute], [DefaultPic], [TurnUrl], [priority], [Rank], [Hits], [HitsByDay], [HitsByWeek], [HitsByMonth], [LastHitsTime], [AddDate], [VoteID], [TemplateFile], [Template3GFile], [ShowON3G], [FileName], [RefreshTF], [Verify], [DelTF], [Inputer], [AttachmentCharge], [AttachmentChargeOnce], [SocialIDs], [CityID], [CountyID], [SEOTitle], [SEOKeyWords], [SEODescription], [CommentNum], [CopyFrom], [Author], [ProvinceID], [ArrGroupID], [ReadPoint], [ChargeType], [PitchTime], [ReadTimes], [DividePercent], [PKID], [InfoPurview], [Channelid], [GoodNum], [BadNum], [FavNum], [ShareNum]) VALUES (3418, N'男子裸身爬闹市区灯柱致交通瘫痪(图)', 1166, N'<p style="text-align: center;">只穿内裤的尼泊尔壮汉爬上弥敦道路中心灯柱上危坐。来源：香港《文汇报》</p><p style="text-indent: 2em;">中新网11月20日电 据香港《文汇报》报道， 尼泊尔籍男子疑不堪失业及妻离子散打击，19日酒后在香港油麻地闹市情绪失控，脱光衣服只剩内裤，爬上弥敦道路中心一支灯柱危坐海报架上，警方需封路让消防员张开救生气垫拯救，双方对峙近2小时，这名男子始被劝服送院检验。受封路影响，弥敦道交通一度瘫痪，多条巴士线须改道。警方将事件列作“有人在危险位置”处理。</p><p style="text-indent: 2em;">爬灯柱危坐尼泊尔籍男子名Khem(38岁)，身材魁梧，他头及脸部轻微擦损，送院敷治无大碍。现场消息称，Khem有香港身份证，但最近失业，其妻更突携子不辞而别，在双重打击之下情绪十分低落。</p><p style="text-indent: 2em;">现场为香港弥敦道近永星里，上址来回共6条行车线，路中央为分隔花槽，并设有路灯柱。19日早晨9时许，Khem疑借酒浇愁，当他带着酒意步经上址时，突情绪激动脱光身上衣物，仅剩一条内裤，之后冲出马路恍如“泰山”般徒手攀上路中一支灯柱，灯柱离地约7米高处装有一个海报架，Khem索性坐在架上，有驾驶人士见状报警。</p><p style="text-indent: 2em;"><strong>弥敦道全线封 开气垫戒备</strong></p><p style="text-indent: 2em;">警方及消防员接报赶至，立即将现场一段弥敦道来回全线封闭，消防员除在北行线路面张开救生气垫戒备外，又在南行线架起升降台接近事主展开游说。其间Khem在灯柱上时站时坐，险象环生，双方对峙近2小时，至上午11时许Khem终被劝服，由消防员协助乘升降台返回地面。</p><p style="text-indent: 2em;">由于他受轻伤，经救护员即场包扎后送院治理。</p><p style="text-indent: 2em;">事发期间由于救援车辆占用路面，加上上班繁忙时段未过，现场一带交通严重挤塞，警方须疏导车辆转入支路，多条巴士线亦须改道，有上班途人对事件大感无奈，直指Khem的行为太自私，但亦有市民得悉事主遭遇后深表同情。</p>', N'', N'', N'只穿内裤的尼泊尔壮汉爬上弥敦道路中心灯柱上危坐。来源：香港《文汇报》中新网11月20日电 据香港《文汇报》报道， 尼泊尔籍男子疑不堪失业及妻离子散打击，19日酒后在香港油麻地闹市情绪失控，脱光衣服只剩内裤，爬上弥敦道路中心一支灯柱危坐海报架上，警方需封路让消防员', N'', N'0000000', N'http://i.kesion.com/UploadFiles/2013-11/74/b1130294063144218750FO8.jpg', N'', 0, NULL, 4, 1, 